def get_tx_func(txid):
    return FIXTURES["transactions"][txid]


def test_sign_deposit():
    rawtx = scripts.sign_deposit(
        get_tx_func, **FIXTURES["deposit"]["input"]
    )
    assert rawtx == FIXTURES["deposit"]["expected"]


